import React from 'react'
import {Menu, SubMenu,Icon} from 'antd'
import './index.less'
import http from '../../util/http'
const MenuItem = Menu.item
const MyIcon = Icon.createFromIconfontCN({
    scriptUrl: '//at.alicdn.com/t/font_977200_ggwbeu0be67.js',
});

class SiderComponent extends React.Component{
    state = {
        menuList: {}
    }
    componentDidMount(){
        http.post("/manage/backUser/checkLogin",{userPhone:15855496761})
            .then(res => {
            this.setState({
                menuList:res.body.roleList
            })
        })

    }
    getMenu () {
        const menu = []
        if (this.state.menuList.length) {
            menu = this.state.menuList.map((item, key) => {
                return <SubMenu key={key} title={<span><Icon type="mail" /><span>item.title</span></span>}>
                if (item.children.length) {
                    item.children.map((sun, i) => {
                        <Menu.Item key={i}>sun.title</Menu.Item>
                    })
                }
                </SubMenu>
            })
        }
        return
    }
    render(){
        const siderMenu = this.getMenu();
        return (
            <div className="logo" >
                <MyIcon className="logoIcon" type="icon-daikuan"/>
                <p>现金贷</p>
                <siderMenu/>
            </div>
        )
    }
}
export default SiderComponent;
